DROP TABLE IF EXISTS `article_ratings`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `article_ratings` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `uuid` char(36) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tenant_id` bigint unsigned DEFAULT NULL,
  `article_id` bigint unsigned NOT NULL,
  `user_id` bigint unsigned DEFAULT NULL,
  `guest_email` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `guest_name` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `rating` tinyint unsigned NOT NULL COMMENT 'Rating from 1 to 5',
  `review` text COLLATE utf8mb4_unicode_ci,
  `criteria_ratings` json DEFAULT NULL,
  `is_verified` tinyint(1) NOT NULL DEFAULT '0',
  `is_helpful` tinyint(1) NOT NULL DEFAULT '0',
  `helpful_count` int NOT NULL DEFAULT '0',
  `not_helpful_count` int NOT NULL DEFAULT '0',
  `status` enum('pending','approved','rejected','spam') COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'pending',
  `moderated_by` bigint unsigned DEFAULT NULL,
  `moderated_at` timestamp NULL DEFAULT NULL,
  `moderation_notes` text COLLATE utf8mb4_unicode_ci,
  `ip_address` varchar(45) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `user_agent` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `article_ratings_uuid_unique` (`uuid`),
  UNIQUE KEY `unique_user_article_rating` (`article_id`,`user_id`),
  UNIQUE KEY `unique_guest_article_rating` (`article_id`,`guest_email`),
  KEY `article_ratings_tenant_id_article_id_index` (`tenant_id`,`article_id`),
  KEY `article_ratings_tenant_id_rating_index` (`tenant_id`,`rating`),
  KEY `article_ratings_tenant_id_status_index` (`tenant_id`,`status`),
  KEY `article_ratings_article_id_rating_index` (`article_id`,`rating`),
  KEY `article_ratings_article_id_status_index` (`article_id`,`status`),
  KEY `article_ratings_rating_created_at_index` (`rating`,`created_at`),
  KEY `article_ratings_tenant_id_index` (`tenant_id`),
  KEY `article_ratings_user_id_index` (`user_id`),
  KEY `article_ratings_is_verified_index` (`is_verified`),
  KEY `article_ratings_is_helpful_index` (`is_helpful`),
  KEY `article_ratings_helpful_count_index` (`helpful_count`),
  KEY `article_ratings_not_helpful_count_index` (`not_helpful_count`),
  KEY `article_ratings_status_index` (`status`),
  KEY `article_ratings_moderated_by_index` (`moderated_by`),
  CONSTRAINT `chk_article_ratings_rating` CHECK ((`rating` between 1 and 5))
) ENGINE=MyISAM AUTO_INCREMENT=15 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
